mapper.select:
select num,name,pass,subject,content,file1 fileurl, regdate, readcnt, ref, reflevel, refstep, tcode from board
where tcode=#{tcode}

	and reflevel=0


	and num = #{num}


	where num = #{del}


	and tcode=#{tcode} and ${column} like '%${find}%' 

 order by ref desc, refstep desc

	limit #{startrow},${limit}
